(2, 'Customer', 'SELECT customer.*,file.dir FROM customer LEFT JOIN file ON file.table_id = customer.id and file.table= ''customer''', '["nama","email","telp","alamat","status","dir"]', 'ENABLE', '2019-03-25 10:47:39', '2019-07-01 09:01:19'),
(3, 'Statistik', 'SELECT date_format(pembayaran_spp.bulan, ''%M %Y'') as bulan, a.belum_bayar, c.sudah_bayar, b.belum_validasi, d.sudah_validasi\r\nFROM pembayaran_spp\r\nLEFT JOIN\r\n	(\r\n        SELECT pembayaran_spp.bulan, COUNT(pembayaran_spp.id) as belum_bayar\r\n        FROM pembayaran_spp\r\n        WHERE pembayaran_spp.tanggal_pembayaran = ''0000-00-00 00:00:00''\r\n        GROUP BY pembayaran_spp.bulan\r\n        ) a\r\nON pembayaran_spp.bulan = a.bulan\r\nLEFT JOIN\r\n	(\r\n        SELECT pembayaran_spp.bulan, COUNT(pembayaran_spp.id) as belum_validasi\r\n        FROM pembayaran_spp\r\n        WHERE pembayaran_spp.tanggal_validasi = ''0000-00-00 00:00:00''\r\n        GROUP BY pembayaran_spp.bulan\r\n        ) b\r\nON pembayaran_spp.bulan = b.bulan\r\nLEFT JOIN\r\n	(\r\n        SELECT pembayaran_spp.bulan, COUNT(pembayaran_spp.id) as sudah_bayar\r\n        FROM pembayaran_spp\r\n        WHERE pembayaran_spp.tanggal_pembayaran != ''0000-00-00 00:00:00''\r\n        GROUP BY pembayaran_spp.bulan\r\n        ) c\r\nON pembayaran_spp.bulan = c.bulan\r\nLEFT JOIN\r\n	(\r\n        SELECT pembayaran_spp.bulan, COUNT(pembayaran_spp.id) as sudah_validasi\r\n        FROM pembayaran_spp\r\n        WHERE pembayaran_spp.tanggal_validasi != ''0000-00-00 00:00:00''\r\n        GROUP BY pembayaran_spp.bulan\r\n        ) d\r\nON pembayaran_spp.bulan = d.bulan\r\nGROUP BY pembayaran_spp.bulan', '["bulan","belum_bayar","belum_validasi","sudah_bayar","sudah_validasi"]', 'ENABLE', '2020-01-07 13:56:11', '2020-01-07 13:58:08'),
(4, 'Laporan Bulanan', 'SELECT form_laporan_bulanan.id,lokasi,master_departemen.nama as departemen,master_bagian.nama as bagian,tanggal,value,kabag.nama as id_kabag,form_laporan_bulanan.status,sr.nama as created_by \r\nFROM form_laporan_bulanan\r\nLEFT JOIN master_departemen on form_laporan_bulanan.departemen = master_departemen.id\r\nLEFT JOIN master_bagian on form_laporan_bulanan.bagian = master_bagian.id\r\nLEFT JOIN pegawai sr on form_laporan_bulanan.created_by = sr.id\r\nLEFT JOIN pegawai kabag on form_laporan_bulanan.id_kabag = kabag.id\r\nWHERE form_laporan_bulanan.status = ''ENABLE''', '["lokasi","departemen","bagian","tanggal","id_kabag","created_by"]', 'ENABLE', '2020-05-29 22:58:48', '2020-05-29 23:03:25'),
(5, 'Laporan Risalah Sidang', 'SELECT hasil_rapat.id,master_jadwal_rapat.nama as id_jadwal,hasil_rapat.pimpinan_sidang,hasil_rapat.tanggal,hasil_rapat.jam_mulai,hasil_rapat.jam_selesai,hasil_rapat.lokasi,pendahuluan,review,tindak_lanjut,materi_tambahan,materi_kesehatan,pegawai.nama as id_notulis,hasil_rapat.status_sidang, master_status_sidang.nama as nama_status\r\nFROM hasil_rapat\r\nLEFT JOIN master_jadwal_rapat on hasil_rapat.id_jadwal = master_jadwal_rapat.id\r\nLEFT JOIN pegawai on hasil_rapat.id_notulis = pegawai.id\r\nLEFT JOIN master_status_sidang on hasil_rapat.status_sidang = master_status_sidang.id\r\nWHERE hasil_rapat.status = ''ENABLE''', '["id_jadwal","pimpinan_sidang","tanggal","jam_mulai","jam_selesai","lokasi","id_notulis","nama_status"]', 'ENABLE', '2020-05-31 02:57:18', '2020-05-31 02:57:50'),
(6, 'Kecelakaan Internal', 'SELECT kecelakaan_main.id, kecelakaan_main.ip_nama, kecelakaan_main.ip_nomor_induk, kecelakaan_main.ip_dep_birobid, kecelakaan_main.ip_bagian_seksi, se.nama as nama_se, kabag.nama as nama_kabag, k3.nama as nama_k3, penderita.nama as nama_penderita, kecelakaan_main.status_kecelakaan, master_status_kecelakaan.nama as nama_status\r\nFROM kecelakaan_main\r\nLEFT JOIN kecelakaan_detail_internal on kecelakaan_main.id = kecelakaan_detail_internal.id_kecelakaan\r\nLEFT JOIN pegawai se on kecelakaan_main.id_se = se.id\r\nLEFT JOIN pegawai kabag on kecelakaan_main.id_kabag = kabag.id\r\nLEFT JOIN pegawai k3 on kecelakaan_main.id_k3 = k3.id\r\nLEFT JOIN pegawai penderita on kecelakaan_main.id_penderita = penderita.id\r\nLEFT JOIN master_status_kecelakaan on kecelakaan_main.status_kecelakaan = master_status_kecelakaan.id\r\nWHERE kecelakaan_detail_internal.status = ''ENABLE''', '["ip_nama","ip_nomor_induk","ip_dep_birobid","ip_bagian_seksi","nama_se","nama_kabag","nama_k3","nama_penderita","status_kecelakaan","nama_status"]', 'ENABLE', '2020-05-31 19:54:10', '2020-05-31 19:54:59'),
(7, 'Kecelakaan Eksternal', 'SELECT kecelakaan_main.id, kecelakaan_main.ip_nama, kecelakaan_main.ip_nomor_induk, kecelakaan_main.ip_dep_birobid, kecelakaan_main.ip_bagian_seksi, se.nama as nama_se, kabag.nama as nama_kabag, k3.nama as nama_k3, penderita.nama as nama_penderita, kecelakaan_main.status_kecelakaan, master_status_kecelakaan.nama as nama_status \r\nFROM kecelakaan_main \r\nLEFT JOIN kecelakaan_detail_ekternal on kecelakaan_main.id = kecelakaan_detail_ekternal.id_kecelakaan \r\nLEFT JOIN pegawai se on kecelakaan_main.id_se = se.id \r\nLEFT JOIN pegawai kabag on kecelakaan_main.id_kabag = kabag.id \r\nLEFT JOIN pegawai k3 on kecelakaan_main.id_k3 = k3.id \r\nLEFT JOIN pegawai penderita on kecelakaan_main.id_penderita = penderita.id\r\nLEFT JOIN master_status_kecelakaan on kecelakaan_main.status_kecelakaan = master_status_kecelakaan.id \r\nWHERE kecelakaan_detail_ekternal.status = ''ENABLE''', '["ip_nama","ip_nomor_induk","ip_dep_birobid","ip_bagian_seksi","nama_se","nama_kabag","nama_k3","nama_penderita","status_kecelakaan","nama_status"]', 'ENABLE', '2020-06-01 04:11:47', '2020-06-01 04:51:10');
